from torchaudio.utils import download_asset
class CTCHypothesis(NamedTuple):
    """Represents hypothesis generated by CTC beam search decoder :class:`CTCDecoder`."""
    tokens: torch.LongTensor
    'Predicted sequence of token IDs. Shape `(L, )`, where `L` is the length of the output sequence'
    words: List[str]
    'List of predicted words.\n\n    Note:\n        This attribute is only applicable if a lexicon is provided to the decoder. If\n        decoding without a lexicon, it will be blank. Please refer to :attr:`tokens` and\n        :func:`~torchaudio.models.decoder.CTCDecoder.idxs_to_tokens` instead.\n    '
    score: float
    'Score corresponding to hypothesis'
    timesteps: torch.IntTensor
    'Timesteps corresponding to the tokens. Shape `(L, )`, where `L` is the length of the output sequence'