from __future__ import absolute_import, division, print_function
from ansible_collections.azure.azcollection.plugins.module_utils.azure_rm_common import AzureRMModuleBase
def keyvault_to_dict(vault):
    return dict(id=vault.id, name=vault.name, location=vault.location, tags=vault.tags, vault_uri=vault.properties.vault_uri, enabled_for_deployment=vault.properties.enabled_for_deployment, enabled_for_disk_encryption=vault.properties.enabled_for_disk_encryption, enabled_for_template_deployment=vault.properties.enabled_for_template_deployment, enable_soft_delete=vault.properties.enable_soft_delete, soft_delete_retention_in_days=vault.properties.soft_delete_retention_in_days if vault.properties.soft_delete_retention_in_days else 90, enable_purge_protection=vault.properties.enable_purge_protection if vault.properties.enable_purge_protection else False, access_policies=[dict(tenant_id=policy.tenant_id, object_id=policy.object_id, permissions=dict(keys=[kp.lower() for kp in policy.permissions.keys] if policy.permissions.keys else None, secrets=[sp.lower() for sp in policy.permissions.secrets] if policy.permissions.secrets else None, certificates=[cp.lower() for cp in policy.permissions.certificates] if policy.permissions.certificates else None, storage=[stp.lower() for stp in policy.permissions.storage] if policy.permissions.storage else None) if policy.permissions else None) for policy in vault.properties.access_policies] if vault.properties.access_policies else None, sku=dict(family=vault.properties.sku.family, name=vault.properties.sku.name))