    self.assertEqual(1 != self.v1.elementwise(), 1 != self.v1.x and 1 != self.v1.y and (1 != self.v1.z))
    self.assertEqual(2 <= self.v1.elementwise(), 2 <= self.v1.x and 2 <= self.v1.y and (2 <= self.v1.z))
