    self.assertEqual(bool(Vector3().elementwise()), bool(Vector3()))
    self.assertEqual(self.zeroVec.elementwise() ** 0, (1, 1, 1))
    self.assertRaises(ValueError, lambda: pow(Vector3(-1, 0, 0).elementwise(), 1.2))
