import base64
import os
from textwrap import dedent
from twisted.conch.test import keydata
from twisted.python import randbytes
from twisted.python.filepath import FilePath
from twisted.python.reflect import requireModule
from twisted.trial import unittest
def test_fromStringErrors(self):
    """
        keys.Key.fromString should raise BadKeyError when the key is invalid.
        """
    self.assertRaises(keys.BadKeyError, keys.Key.fromString, b'')
    self.assertRaises(keys.BadKeyError, keys.Key.fromString, b'', 'bad_type')
    self.assertRaises(keys.BadKeyError, keys.Key.fromString, keydata.publicRSA_lsh, passphrase=b'unencrypted')
    self.assertRaises(keys.EncryptedKeyError, keys.Key.fromString, keys.Key(self.rsaObj).toString('openssh', passphrase=b'encrypted'))
