from openstack.object_store.v1 import info
from openstack.tests.unit import base
def test_get_info_url(self):
    sot = info.Info()
    test_urls = {'http://object.cloud.example.com': 'http://object.cloud.example.com/info', 'http://object.cloud.example.com/': 'http://object.cloud.example.com/info', 'http://object.cloud.example.com/v1': 'http://object.cloud.example.com/info', 'http://object.cloud.example.com/v1/': 'http://object.cloud.example.com/info', 'http://object.cloud.example.com/swift': 'http://object.cloud.example.com/swift/info', 'http://object.cloud.example.com/swift/': 'http://object.cloud.example.com/swift/info', 'http://object.cloud.example.com/v1.0': 'http://object.cloud.example.com/info', 'http://object.cloud.example.com/swift/v1.0': 'http://object.cloud.example.com/swift/info', 'http://object.cloud.example.com/v111': 'http://object.cloud.example.com/info', 'http://object.cloud.example.com/v111/test': 'http://object.cloud.example.com/info', 'http://object.cloud.example.com/v1/test': 'http://object.cloud.example.com/info', 'http://object.cloud.example.com/swift/v1.0/test': 'http://object.cloud.example.com/swift/info', 'http://object.cloud.example.com/v1.0/test': 'http://object.cloud.example.com/info', 'https://object.cloud.example.com/swift/v1/AUTH_%(tenant_id)s': 'https://object.cloud.example.com/swift/info', 'https://object.cloud.example.com/swift/v1/AUTH_%(project_id)s': 'https://object.cloud.example.com/swift/info', 'https://object.cloud.example.com/services/swift/v1/AUTH_%(project_id)s': 'https://object.cloud.example.com/services/swift/info', 'https://object.cloud.example.com/services/swift/v1/AUTH_%(project_id)s/': 'https://object.cloud.example.com/services/swift/info', 'https://object.cloud.example.com/info/v1/AUTH_%(project_id)s/': 'https://object.cloud.example.com/info/info'}
    for uri_k, uri_v in test_urls.items():
        self.assertEqual(sot._get_info_url(uri_k), uri_v)