import _plotly_utils.basevalidators
class TitleValidator(_plotly_utils.basevalidators.TitleValidator):

    def __init__(self, plotly_name='title', parent_name='surface.colorbar', **kwargs):
        super(TitleValidator, self).__init__(plotly_name=plotly_name, parent_name=parent_name, data_class_str=kwargs.pop('data_class_str', 'Title'), data_docs=kwargs.pop('data_docs', '\n            font\n                Sets this color bar\'s title font. Note that the\n                title\'s font used to be set by the now\n                deprecated `titlefont` attribute.\n            side\n                Determines the location of color bar\'s title\n                with respect to the color bar. Defaults to\n                "top" when `orientation` if "v" and  defaults\n                to "right" when `orientation` if "h". Note that\n                the title\'s location used to be set by the now\n                deprecated `titleside` attribute.\n            text\n                Sets the title of the color bar. Note that\n                before the existence of `title.text`, the\n                title\'s contents used to be defined as the\n                `title` attribute itself. This behavior has\n                been deprecated.\n'), **kwargs)