def AddMaintenanceExclusionFlags(parser, hidden=False, enable_scope=True):
    """Adds flags related to adding a maintenance exclusion to the parser."""
    help_text = 'Sets a period of time in which maintenance should not occur. This is compatible\nwith both daily and recurring maintenance windows.\n+\nExamples:\n+\n  $ {command} example-cluster   --add-maintenance-exclusion-name=holidays-2000   --add-maintenance-exclusion-start=2000-11-20T00:00:00   --add-maintenance-exclusion-end=2000-12-31T23:59:59\n'
    if enable_scope:
        help_text = 'Sets a period of time in which maintenance should not occur. This is compatible\nwith both daily and recurring maintenance windows.\nIf `--add-maintenance-exclusion-scope` is not specified, the exclusion will\nexclude all upgrades.\n+\nExamples:\n+\n  $ {command} example-cluster   --add-maintenance-exclusion-name=holidays-2000   --add-maintenance-exclusion-start=2000-11-20T00:00:00   --add-maintenance-exclusion-end=2000-12-31T23:59:59   --add-maintenance-exclusion-scope=no_upgrades\n'
    group = parser.add_group(hidden=hidden, help=help_text)
    group.add_argument('--add-maintenance-exclusion-name', type=str, metavar='NAME', help='A descriptor for the exclusion that can be used to remove it. If not specified,\nit will be autogenerated.\n')
    group.add_argument('--add-maintenance-exclusion-start', type=arg_parsers.Datetime.Parse, metavar='TIME_STAMP', help='Start time of the exclusion window (can occur in the past). If not specified,\nthe current time will be used. See $ gcloud topic datetimes for information on\ntime formats.\n')
    group.add_argument('--add-maintenance-exclusion-end', type=arg_parsers.Datetime.Parse, required=True, metavar='TIME_STAMP', help='End time of the exclusion window. Must take place after the start time. See\n$ gcloud topic datetimes for information on time formats.\n')
    group.add_argument('--add-maintenance-exclusion-scope', type=arg_parsers.RegexpValidator('^(no_upgrades|no_minor_upgrades|no_minor_or_node_upgrades)$', 'Must be in one of "no_upgrades", "no_minor_upgrades" or "no_minor_or_node_upgrades"'), required=False, metavar='SCOPE', help='Scope of the exclusion window to specify the type of upgrades that the exclusion\nwill apply to. Must be in one of no_upgrades, no_minor_upgrades or no_minor_or_node_upgrades.\nIf not specified in an exclusion, defaults to no_upgrades.\n', hidden=not enable_scope)
    parser.add_argument('--remove-maintenance-exclusion', type=str, hidden=hidden, metavar='NAME', help="Name of a maintenance exclusion to remove. If you hadn't specified a name, one\nwas auto-generated. Get it with $ gcloud container clusters describe.\n")